import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

import './styles.css';

class App extends React.Component {
    state = {
    doggos: [],
    doggoText: 'husky'
    };

    /*
    const [doggoText, setDoggoText] = useState('husky');
    const [url, setUrl] = useState(`https://dog.ceo/api/breed/${doggoText}/images`)
    const [doggos, setDoggos] = useState([]);
    useEffect(() => {
        axios
        .get(url)
        .then(res => {
            setDoggos(res.data.message);
        })
        .catch(err => console.log(err));
    }, [url])

    fetchDoggos = e => {
        e.preventDefault();
        if (doggoText !== chihuahua) {
        setUrl(`https://dog.ceo/api/breed/${doggoText}/images`);
        } else {
        setUrl(`https://dog.ceo/api/breed/husky/images`);
        setDoggoText('husky');
        }
    }
    */

    componentDidMount() {
      // useEffect
    axios
        .get('https://api.github.com/users')
        .then(res => {
        this.setState({
            doggos: res.data.message
        });
        })
        .catch(err => console.log(err));

    window.addEventListener('resize', this.handleResize);
    }

    componentDidUpdate(prevProps, prevState) {
    if (this.state.doggos !== prevState.doggos) {
        // block chihuahua images ðŸ‘¿
        if (this.state.doggoText === 'chihuahua') {
        axios
            .get('https://dog.ceo/api/breed/husky/images')
            .then(res => {
            this.setState({
                doggos: res.data.message,
                doggoText: 'husky'
            });
            })
            .catch(err => console.log(err));
        }
    }
    }

    handleChanges = e => {
    this.setState({
        doggoText: e.target.value
    });
    };

    fetchDoggos = e => {
    e.preventDefault();
      // fetch new doggo images based on the input text
      // update state with the new doggo images array
    axios
        .get(`https://dog.ceo/api/breed/${this.state.doggoText}/images`)
        .then(res => {
        this.setState({
            doggos: res.data.message
        });
        })
        .catch(err => console.log(err));
    };

    render() {
    return (
        <div className="App">
        <h1>Hello Doggos</h1>
        <input
            type="text"
            value={this.state.doggoText}
            onChange={this.handleChanges}
        />
        <button onClick={this.fetchDoggos}>Fetch doggos</button>
        <div className="doggos">
            {this.state.doggos.map(doggo => (
            <img width="200" src={doggo} key={doggo} alt={doggo} />
            ))}
        </div>
        </div>
    );
    }
}

export default App;
